// Please do not change the name of this function 
 function getTweetData (tweet) { 
 // Your code here 
 const tweetObj = { 
 tags: [], 
 mentions: [], 
 tagCount: 0, 
 mentionCount: 0, 
 length: tweet.length
 } 

const mentionRegex = /@[a-z]+/gi;
const hashtagRegex = /#[a-z]+/gi;
//count mentions and populate mentions array
if (mentionRegex.test(tweet)){
tweetObj.mentions = tweet.match(mentionRegex);
}
tweetObj.mentions = tweetObj.mentions.filter((value, index) => tweetObj.mentions.indexOf(value) === index);
tweetObj.mentionCount = tweetObj.mentions.length;
//count hashtags and populate tag array 
if (hashtagRegex.test(tweet)){
tweetObj.tags = tweet.match(hashtagRegex);
}
tweetObj.tags = tweetObj.tags.filter((value, index) => tweetObj.tags.indexOf(value) === index);
tweetObj.tagCount = tweetObj.tags.length;
return tweetObj;
};
module.exports = getTweetData;
